import React, { useState, useEffect } from 'react';
import axios from "axios";
//components
import Header from "./components/Header";
import Todo from "./components/Todo";
import Form from './components/Form';

function App() {
  const baseURL = "https://dummyjson.com/todos/";

  const [todoList, setToDoList] = useState([]);
  const [filter, setChecked] = useState(false);

  const fetchList = () => {
    axios.get(baseURL)
      .then((response) => {
        setToDoList(response.data.todos);
      })
      .catch(error => {
        alert("Server error: " + error);
        setToDoList([])
      });
  }

  const completeTask = (id) => {
    alert(id)
    let item = todoList.find(task => { return task.id === id; })
    axios.post(baseURL + id, { completed: !item.completed })
      .then((response) => {
        alert(response.data);
      })
      .catch(error => {
        alert("Server error: " + error);
      });
  }

  useEffect(() => { fetchList() }, []);

  const handleToggle = (id) => {
    completeTask(id)
    let mapped = todoList.map(task => {
      return task.id === Number(id) ? { ...task, completed: !task.completed } : { ...task };
    });
    setToDoList(mapped);
  }

  const handleEdit = (id) => {
    // TODO: Routing to editor's page
    alert("https://dummyjson.com/todos/" + id);
  }

  const handleFilter = () => {
    setChecked(!filter)
    if (filter) {
      fetchList();
    }
    else {
      let filtered = todoList.filter(task => { return !task.completed; });
      setToDoList(filtered);
    }
  }

  const addTask = (userInput) => {
    let copy = [...todoList];
    copy = [...copy, { id: todoList.length + 1, todo: userInput, completed: false, userId: 13 }];
    setToDoList(copy);
  }

  return (
    <div className="App">
      <Header />
      <Form addTask={addTask} />
      <div>
        <input type="checkbox" onChange={handleFilter} />
        <label>Show only active tasks</label>
      </div>
      <ul>
        {todoList.map(todo => {
          return (
            <Todo task={todo} handleToggle={handleToggle} handleEdit={handleEdit} />
          )
        })}
      </ul>
    </div>
  );
}

export default App;
