import React from 'react';
import "./Todo.css"

const ToDo = ({ todo, handleToggle, handleEdit}) => {

    const handleRemoveClick = (e) => {
        alert(e.currentTarget.id)
        e.preventDefault()
        handleToggle(e.currentTarget.id)
    }

    const handleEditClick = (e) => {
        alert(e.currentTarget.id)
        e.preventDefault()
        handleEdit(e.currentTarget.id)
    }

    return (
        <li className="todo stack-small" >
            <div className="c-cb" id={todo.id}>
                <input type="checkbox" checked = {todo.complete}/>
                <label className="todo-label" htmlFor={todo.id} decoration = "through">
                    {todo.task}
                </label>
                <span className="edit" id= {todo.id} onClick={handleEditClick}>&#9998;</span>
                <span className="close" id= {todo.id} onClick={handleRemoveClick}>&#215;</span>
            </div>
        </li>
    );
};

export default ToDo;
