import React from 'react';
import "./Todo.css"

const ToDo = ({ task, handleToggle, handleEdit}) => {

    const handleRemoveClick = (e) => {
        e.preventDefault()
        handleToggle(e.currentTarget.id)
    }

    const handleEditClick = (e) => {
        e.preventDefault()
        handleEdit(e.currentTarget.id)
    }

    return (
        <li className="todo stack-small" >
            <div id={task.id} className={task.completed ? "strike todo" : "todo"}>
                <label className="todo-label" htmlFor={todo.id}>
                    {task.todo}
                </label>
                <span className="edit" id= {todo.id} onClick={handleEditClick}>&#9998;</span>
                <span className="close" id= {todo.id} onClick={handleRemoveClick}>{todo.completed? '\u21BB' :'\xd7'}</span>
            </div>
        </li>
    );
};

export default ToDo;
