import React, { useState, useEffect } from 'react';
import axios from "axios";
//components
import Header from "./components/Header";
import Todo from "./components/Todo";
import Form from './components/Form';

function App() {
  const baseURL = "https://dummyjson.com/todos/";

  const [todoList, setToDoList] = useState([]);
  const [filter, setChecked] = useState(false);

  const fetchList = () => {
    axios.get(baseURL)
      .then((response) => {
        setToDoList(response.data.todos);
      })
      .catch(error => {
        alert("Server error: " + error);
        setToDoList([])
      });
  }

  const completeTask = (id) => {
    let url = baseURL + id
    let status = todoList.find(task => { return task.id == id; }).completed
    axios.put(url, { completed: !status })
      .then((response) => {
        let updated = response.data
        alert("PUT: Item ID " + updated.id + " : Completed = " + updated.completed)
        // Fetch updated list
        fetchList()
      })
      .catch(error => {
        alert("Server error: " + error);
      });
  }

  const addNewTask = (name) => {
    let url = baseURL + "add"
    axios.post(url, { todo: name, completed: false, userId: 5 })
      .then((response) => {
        let created = response.data
        alert("Added: Item ID " + created.id + " : " + created.todo)
        fetchList()
      })
      .catch(error => {
        alert("Server error: " + error);
      });
  }

  const applyFilter = () => {
    if (filter) {
      fetchList();
    }
    else {
      let filtered = todoList.filter(task => { return !task.completed; });
      setToDoList(filtered);
    }
  }

  useEffect(() => { fetchList() }, []);

  const handleToggle = (id) => {
    completeTask(id)
  }

  const handleEdit = (id) => {
    // TODO: Routing to editor's page
    alert(baseURL + id);
  }

  const handleFilter = () => {
    setChecked(!filter)
    applyFilter()
  }

  const addTask = (userInput) => {
    addNewTask(userInput)
    //let copy = [...todoList];
    //copy = [...copy, { id: todoList.length + 1, todo: userInput, completed: false, userId: 5 }];
    //setToDoList(copy);
  }

  return (
    <div className="App">
      <Header />
      <Form addTask={addTask} />
      <div>
        <input type="checkbox" onChange={handleFilter} />
        <label>Show only active tasks</label>
      </div>
      <ul>
        {todoList.map(todo => {
          return (
            <Todo task={todo} handleToggle={handleToggle} handleEdit={handleEdit} />
          )
        })}
      </ul>
    </div>
  );
}

export default App;
